import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'
import MyNavbar from '../components/MyNavbar';
import ProductsData from '../ProductsData';

const ProductsView = () => {
    
    const [search, setSearch] = useState("");
    const [sortBy, setSortBy] = useState("");

    const handleChangeSortBy = e => {
        setSortBy(e.target.value);
    }
    
    const handleChangeInput = e => {
        setSearch(e.target.value);
    }

    const results = !search ? ProductsData : ProductsData.filter((product) => 
        product.name.includes(search)
    )

    const resultsL = results.price.sort((a, b) => a-b)
    const resultsH = results.price.sort((a, b) => b-a)

    const productsList = (() => {
        if(sortBy === ''){
            return results;
        } else if(sortBy === 'Lower'){
            return resultsL;
        } else if(sortBy === 'Higher'){
            return resultsH;
        }
    })

    return (
        <div>
            <MyNavbar />
            <div className="section">
                <h2>Products</h2>
                <div>
                    <input placeholder="Search" value={search} onChange={handleChangeInput}></input>
                    <form action="">
                        <select id="sortby" name="sortby" value={sortBy} onChange={handleChangeSortBy}>
                            <option value="" disabled selected>Sort By</option>
                            <option value="Lower">Lower to Higher Price</option>
                            <option value="Higher">Higher to Lower Price</option>
                        </select>
                    </form>
                </div>
                <div className="products">
                    {productsList.map((product, idx) => (
                        <div className="product-card" key={idx}>
                            <Link to={`/product-details/${product.id}`}>
                                <img src={product.image} alt={product.name} />
                            </Link>
                            <h3>
                                <Link to={`/product-details/${product.id}`}>{product.name}</Link>
                            </h3>
                            <h6>{product.price}</h6>
                            <button>
                                Add to Cart
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default ProductsView;
