import React from 'react';

const Product = (props) => {
    
    return (
        <div className="product-card">
            <img src={props.pic} alt={props.name} />
            <h3>{props.name}</h3>
            <h6>C.I: V.- {props.id}</h6>
            <p>{props.description}</p>
            <button href={props.id}>More Info</button>
        </div>
    );
}
 
export default Product;
